using System;
using System.Web.Http;
using WebApi.Models;

namespace WebApi.Controllers
{
    public class CaptchaController : ApiController
    {

        public IHttpActionResult GetCaptcha(int id)
        {
            CaptchaInformation captchaInformation = new CaptchaInformation()
            {
                ImageBase64 = "",
                QuestionType = CaptchaInformation.QUESTION_TYPE_GREATER,
                Options = new int[] { 20,45,65,44,12,100 },
                Id = Guid.NewGuid()
            };
            return Ok(captchaInformation);
        }

    }
}
